import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import RippleGrid from '../components/RippleGrid';
import GooeyNav from '../components/GooeyNav';
import { ReactComponent as EuroLogo } from '../assets/logos/euro-logo.svg';
import { getUserNotifications, markAsRead, markAllAsRead, deleteNotification } from '../utils/notificationManager';
import './NotificationsPage.css';

function NotificationsPage() {
  const navigate = useNavigate();
  const [notifications, setNotifications] = useState([]);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [notificationToDelete, setNotificationToDelete] = useState(null);
  const [showBulkDeleteModal, setShowBulkDeleteModal] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 4;

  useEffect(() => {
    // –ü—Ä–æ–≤–µ—Ä—è–µ–º –∞–≤—Ç–æ—Ä–∏–∑–∞—Ü–∏—é
    const user = localStorage.getItem('user');
    if (!user) {
      navigate('/');
      return;
    }

    // –ó–∞–≥—Ä—É–∂–∞–µ–º —É–≤–µ–¥–æ–º–ª–µ–Ω–∏—è
    loadNotifications();
  }, [navigate]);

  const loadNotifications = () => {
    const user = JSON.parse(localStorage.getItem('user'));
    const username = user?.username || 'unknown';
    const userNotifications = getUserNotifications(username);
    setNotifications(userNotifications);
  };

  const handleMarkAsRead = (notificationId) => {
    markAsRead(notificationId);
    loadNotifications();
  };

  const handleMarkAllAsRead = () => {
    const user = JSON.parse(localStorage.getItem('user'));
    const username = user?.username || 'unknown';
    markAllAsRead(username);
    loadNotifications();
  };

  const handleDeleteClick = (notification) => {
    setNotificationToDelete(notification);
    setShowDeleteModal(true);
  };

  const handleConfirmDelete = () => {
    if (notificationToDelete) {
      deleteNotification(notificationToDelete.id);
      loadNotifications();
      setShowDeleteModal(false);
      setNotificationToDelete(null);
      
      // –ü—Ä–æ–≤–µ—Ä—è–µ–º, –Ω–µ –æ—Å—Ç–∞–ª–∞—Å—å –ª–∏ —Ç–µ–∫—É—â–∞—è —Å—Ç—Ä–∞–Ω–∏—Ü–∞ –ø—É—Å—Ç–æ–π
      const newTotalPages = Math.ceil((notifications.length - 1) / itemsPerPage);
      if (currentPage > newTotalPages && newTotalPages > 0) {
        setCurrentPage(newTotalPages);
      }
    }
  };

  const handleCancelDelete = () => {
    setShowDeleteModal(false);
    setNotificationToDelete(null);
  };

  const handleBulkDeleteClick = () => {
    setShowBulkDeleteModal(true);
  };

  const handleDeleteAll = () => {
    const user = JSON.parse(localStorage.getItem('user'));
    const username = user?.username || 'unknown';
    
    // –£–¥–∞–ª—è–µ–º –≤—Å–µ —É–≤–µ–¥–æ–º–ª–µ–Ω–∏—è –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
    const allNotifications = JSON.parse(localStorage.getItem('notifications') || '[]');
    const filteredNotifications = allNotifications.filter(n => n.recipient !== username && n.recipient !== 'all');
    localStorage.setItem('notifications', JSON.stringify(filteredNotifications));
    
    setShowBulkDeleteModal(false);
    loadNotifications();
    setCurrentPage(1);
  };

  const handleDeleteReadOnly = () => {
    const user = JSON.parse(localStorage.getItem('user'));
    const username = user?.username || 'unknown';
    
    // –£–¥–∞–ª—è–µ–º —Ç–æ–ª—å–∫–æ –ø—Ä–æ—á–∏—Ç–∞–Ω–Ω—ã–µ —É–≤–µ–¥–æ–º–ª–µ–Ω–∏—è
    const allNotifications = JSON.parse(localStorage.getItem('notifications') || '[]');
    const filteredNotifications = allNotifications.filter(n => {
      if (n.recipient === username || n.recipient === 'all') {
        return !n.read; // –û—Å—Ç–∞–≤–ª—è–µ–º —Ç–æ–ª—å–∫–æ –Ω–µ–ø—Ä–æ—á–∏—Ç–∞–Ω–Ω—ã–µ
      }
      return true; // –û—Å—Ç–∞–≤–ª—è–µ–º —É–≤–µ–¥–æ–º–ª–µ–Ω–∏—è –¥—Ä—É–≥–∏—Ö –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π
    });
    localStorage.setItem('notifications', JSON.stringify(filteredNotifications));
    
    setShowBulkDeleteModal(false);
    loadNotifications();
    
    // –ü—Ä–æ–≤–µ—Ä—è–µ–º, –Ω–µ –æ—Å—Ç–∞–ª–∞—Å—å –ª–∏ —Ç–µ–∫—É—â–∞—è —Å—Ç—Ä–∞–Ω–∏—Ü–∞ –ø—É—Å—Ç–æ–π
    const newNotifications = getUserNotifications(username);
    const newTotalPages = Math.ceil(newNotifications.length / itemsPerPage);
    if (currentPage > newTotalPages && newTotalPages > 0) {
      setCurrentPage(newTotalPages);
    }
  };

  const handleCancelBulkDelete = () => {
    setShowBulkDeleteModal(false);
  };

  const handleBack = () => {
    navigate('/home');
  };

  const handleNavClick = (item) => {
    if (item.action === 'home') {
      navigate('/home');
    } else if (item.action === 'history') {
      navigate('/history');
    } else if (item.action === 'notifications') {
      // –£–∂–µ –Ω–∞ —Å—Ç—Ä–∞–Ω–∏—Ü–µ —É–≤–µ–¥–æ–º–ª–µ–Ω–∏–π
      return;
    } else if (item.action === 'admin') {
      navigate('/admin');
    } else if (item.action === 'logout') {
      localStorage.removeItem('user');
      navigate('/');
    }
  };

  // –ü—Ä–æ–≤–µ—Ä—è–µ–º —Ä–æ–ª—å –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è –¥–ª—è –æ—Ç–æ–±—Ä–∞–∂–µ–Ω–∏—è –∫–Ω–æ–ø–∫–∏ –∞–¥–º–∏–Ω-–ø–∞–Ω–µ–ª–∏
  const user = JSON.parse(localStorage.getItem('user') || '{}');
  const isAdmin = user.role === 'admin';

  const navItems = isAdmin ? [
    { label: '–ì–ª–∞–≤–Ω–∞—è', href: '#', action: 'home' },
    { label: '–ò—Å—Ç–æ—Ä–∏—è', href: '#', action: 'history' },
    { label: '–£–≤–µ–¥–æ–º–ª–µ–Ω–∏—è', href: '#', action: 'notifications' },
    { label: '–ê–¥–º–∏–Ω', href: '#', action: 'admin' },
    { label: '–í—ã—Ö–æ–¥', href: '#', action: 'logout' }
  ] : [
    { label: '–ì–ª–∞–≤–Ω–∞—è', href: '#', action: 'home' },
    { label: '–ò—Å—Ç–æ—Ä–∏—è', href: '#', action: 'history' },
    { label: '–£–≤–µ–¥–æ–º–ª–µ–Ω–∏—è', href: '#', action: 'notifications' },
    { label: '–í—ã—Ö–æ–¥', href: '#', action: 'logout' }
  ];

  // –í—ã—á–∏—Å–ª—è–µ–º –ø–∞–≥–∏–Ω–∞—Ü–∏—é
  const totalPages = Math.ceil(notifications.length / itemsPerPage);
  const startIndex = (currentPage - 1) * itemsPerPage;
  const endIndex = startIndex + itemsPerPage;
  const currentNotifications = notifications.slice(startIndex, endIndex);

  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
    // –ü—Ä–æ–∫—Ä—É—á–∏–≤–∞–µ–º –∫ –Ω–∞—á–∞–ª—É —Å–ø–∏—Å–∫–∞ —É–≤–µ–¥–æ–º–ª–µ–Ω–∏–π
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  return (
    <div className="notifications-page">
      {/* Ripple Grid —Ñ–æ–Ω */}
      <div className="notifications-background">
        <RippleGrid
          enableRainbow={false}
          gridColor="#8a5cff"
          rippleIntensity={0.01}
          gridSize={20}
          gridThickness={20}
          fadeDistance={2.5}
          vignetteStrength={1.5}
          glowIntensity={0.2}
          mouseInteraction={true}
          mouseInteractionRadius={1.2}
          opacity={0.9}
        />
      </div>

      {/* –õ–æ–≥–æ—Ç–∏–ø –∏ –Ω–∞–≤–∏–≥–∞—Ü–∏—è */}
      <div className="page-header">
        <div className="page-logo">
          <EuroLogo />
        </div>
        <GooeyNav
          items={navItems}
          particleCount={15}
          particleDistances={[90, 10]}
          particleR={100}
          initialActiveIndex={2}
          animationTime={600}
          timeVariance={300}
          colors={[1, 2, 3, 1, 2, 3, 1, 4]}
          onItemClick={handleNavClick}
        />
      </div>

      {/* –°–æ–¥–µ—Ä–∂–∏–º–æ–µ —Å—Ç—Ä–∞–Ω–∏—Ü—ã —É–≤–µ–¥–æ–º–ª–µ–Ω–∏–π */}
      <div className="notifications-content">
        <div className="notifications-header">
          <div className="notifications-header-left">
            <button className="btn-back" onClick={handleBack}>
              ‚Üê –ù–∞–∑–∞–¥
            </button>
            <h1 className="notifications-title">–£–≤–µ–¥–æ–º–ª–µ–Ω–∏—è</h1>
          </div>
          {notifications.length > 0 && (
            <div className="notifications-header-actions">
              <button className="btn-mark-all-read" onClick={handleMarkAllAsRead}>
                –û—Ç–º–µ—Ç–∏—Ç—å –≤—Å–µ –∫–∞–∫ –ø—Ä–æ—á–∏—Ç–∞–Ω–Ω—ã–µ
              </button>
              <button className="btn-delete-all" onClick={handleBulkDeleteClick}>
                –£–¥–∞–ª–∏—Ç—å –≤—Å–µ
              </button>
            </div>
          )}
        </div>

        <div className="notifications-list">
          {notifications.length === 0 ? (
            <div className="empty-notifications">
              <div className="empty-icon">üîî</div>
              <p>–£ –≤–∞—Å –ø–æ–∫–∞ –Ω–µ—Ç —É–≤–µ–¥–æ–º–ª–µ–Ω–∏–π</p>
            </div>
          ) : (
            currentNotifications.map((notification) => (
              <div 
                key={notification.id} 
                className={`notification-item ${notification.read ? 'read' : 'unread'} ${notification.type}`}
              >
                <div className="notification-icon">
                  {notification.type === 'approved' && '‚úì'}
                  {notification.type === 'rejected' && '‚úó'}
                  {notification.type === 'info' && '‚Ñπ'}
                </div>
                
                <div className="notification-content">
                  <h3>{notification.fileName}</h3>
                  <p>{notification.message}</p>
                  <span className="notification-time">
                    {new Date(notification.timestamp).toLocaleString('ru-RU')}
                  </span>
                </div>

                <div className="notification-actions">
                  {!notification.read && (
                    <button 
                      className="btn-mark-read"
                      onClick={() => handleMarkAsRead(notification.id)}
                      title="–û—Ç–º–µ—Ç–∏—Ç—å –∫–∞–∫ –ø—Ä–æ—á–∏—Ç–∞–Ω–Ω–æ–µ"
                    >
                      ‚úì
                    </button>
                  )}
                  <button 
                    className="btn-delete-notification"
                    onClick={() => handleDeleteClick(notification)}
                    title="–£–¥–∞–ª–∏—Ç—å"
                  >
                    üóëÔ∏è
                  </button>
                </div>
              </div>
            ))
          )}
        </div>

        {/* –ü–∞–≥–∏–Ω–∞—Ü–∏—è */}
        {totalPages > 1 && (
          <div className="pagination">
            <button
              className="pagination-btn"
              onClick={() => handlePageChange(currentPage - 1)}
              disabled={currentPage === 1}
            >
              ‚Üê –ù–∞–∑–∞–¥
            </button>
            
            <div className="pagination-pages">
              {Array.from({ length: totalPages }, (_, i) => i + 1).map((pageNum) => (
                <button
                  key={pageNum}
                  className={`pagination-page ${currentPage === pageNum ? 'active' : ''}`}
                  onClick={() => handlePageChange(pageNum)}
                >
                  {pageNum}
                </button>
              ))}
            </div>

            <button
              className="pagination-btn"
              onClick={() => handlePageChange(currentPage + 1)}
              disabled={currentPage === totalPages}
            >
              –í–ø–µ—Ä—ë–¥ ‚Üí
            </button>
          </div>
        )}
      </div>

      {/* –ú–æ–¥–∞–ª—å–Ω–æ–µ –æ–∫–Ω–æ —É–¥–∞–ª–µ–Ω–∏—è */}
      {showDeleteModal && (
        <div className="modal-overlay" onClick={handleCancelDelete}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <h2 className="modal-title">–£–¥–∞–ª–∏—Ç—å —É–≤–µ–¥–æ–º–ª–µ–Ω–∏–µ?</h2>
            <p className="modal-text">
              –í—ã —É–≤–µ—Ä–µ–Ω—ã, —á—Ç–æ —Ö–æ—Ç–∏—Ç–µ —É–¥–∞–ª–∏—Ç—å —ç—Ç–æ —É–≤–µ–¥–æ–º–ª–µ–Ω–∏–µ?
            </p>
            <div className="modal-buttons">
              <button className="modal-btn modal-btn-cancel" onClick={handleCancelDelete}>
                –û—Ç–º–µ–Ω–∞
              </button>
              <button className="modal-btn modal-btn-confirm" onClick={handleConfirmDelete}>
                –£–¥–∞–ª–∏—Ç—å
              </button>
            </div>
          </div>
        </div>
      )}

      {/* –ú–æ–¥–∞–ª—å–Ω–æ–µ –æ–∫–Ω–æ –º–∞—Å—Å–æ–≤–æ–≥–æ —É–¥–∞–ª–µ–Ω–∏—è */}
      {showBulkDeleteModal && (
        <div className="modal-overlay" onClick={handleCancelBulkDelete}>
          <div className="modal-content bulk-delete-modal" onClick={(e) => e.stopPropagation()}>
            <h2 className="modal-title">–£–¥–∞–ª–∏—Ç—å —É–≤–µ–¥–æ–º–ª–µ–Ω–∏—è</h2>
            <p className="modal-text">
              –í—ã–±–µ—Ä–∏—Ç–µ, –∫–∞–∫–∏–µ —É–≤–µ–¥–æ–º–ª–µ–Ω–∏—è —Ö–æ—Ç–∏—Ç–µ —É–¥–∞–ª–∏—Ç—å:
            </p>
            <div className="bulk-delete-options">
              <button className="bulk-option-btn delete-all-btn" onClick={handleDeleteAll}>
                <span className="bulk-option-icon">üóëÔ∏è</span>
                <span className="bulk-option-text">
                  <strong>–£–¥–∞–ª–∏—Ç—å –≤—Å–µ</strong>
                  <small>–£–¥–∞–ª–∏—Ç—å –≤—Å–µ —É–≤–µ–¥–æ–º–ª–µ–Ω–∏—è ({notifications.length})</small>
                </span>
              </button>
              <button className="bulk-option-btn delete-read-btn" onClick={handleDeleteReadOnly}>
                <span className="bulk-option-icon">‚úì</span>
                <span className="bulk-option-text">
                  <strong>–í—Å–µ –ø—Ä–æ—á–∏—Ç–∞–Ω–Ω—ã–µ</strong>
                  <small>–£–¥–∞–ª–∏—Ç—å —Ç–æ–ª—å–∫–æ –ø—Ä–æ—á–∏—Ç–∞–Ω–Ω—ã–µ ({notifications.filter(n => n.read).length})</small>
                </span>
              </button>
            </div>
            <div className="modal-buttons">
              <button className="modal-btn modal-btn-cancel" onClick={handleCancelBulkDelete}>
                –û—Ç–º–µ–Ω–∞
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default NotificationsPage;
