import React, { useState, useRef } from 'react';
import { ReactComponent as DropFileIcon } from '../../assets/icons/DropFileIcon.svg';
import { getRequirements } from '../../utils/videoValidator';
import './VideoUpload.css';

export default function VideoUpload({ onFileSelect, selectedFile, onCancel }) {
  const [isDragging, setIsDragging] = useState(false);
  const [error, setError] = useState('');
  const fileInputRef = useRef(null);
  const requirements = getRequirements();

  const handleDragOver = (e) => {
    e.preventDefault();
    setIsDragging(true);
  };

  const handleDragLeave = (e) => {
    e.preventDefault();
    setIsDragging(false);
  };

  const validateFile = (file) => {
    setError('');
    
    // Проверка типа файла
    if (!file.type.startsWith('video/')) {
      setError('Пожалуйста, выберите видео файл');
      return false;
    }
    
    // Проверка размера файла
    if (file.size > requirements.maxFileSize) {
      setError(`Размер файла превышает ${requirements.maxFileSizeMB} МБ`);
      return false;
    }
    
    return true;
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setIsDragging(false);
    
    const files = e.dataTransfer.files;
    if (files.length > 0) {
      const file = files[0];
      if (validateFile(file)) {
        onFileSelect(file);
      }
    }
  };

  const handleFileInput = (e) => {
    const files = e.target.files;
    if (files.length > 0) {
      const file = files[0];
      if (validateFile(file)) {
        onFileSelect(file);
      }
    }
  };

  const handleClick = () => {
    fileInputRef.current?.click();
  };

  const formatFileSize = (bytes) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return Math.round(bytes / Math.pow(k, i)) + ' ' + sizes[i];
  };

  return (
    <div className="video-upload-container">
      <h2 className="upload-title">Загрузить</h2>
      
      {!selectedFile ? (
        <div
          className={`drop-zone ${isDragging ? 'dragging' : ''}`}
          onDragOver={handleDragOver}
          onDragLeave={handleDragLeave}
          onDrop={handleDrop}
          onClick={handleClick}
        >
          <DropFileIcon className="drop-icon" />
          <p className="drop-text">Перетяните ваш файл или загрузите с устройства</p>
          <p className="drop-limit">Максимальный размер файла 250 МВ</p>
          <input
            ref={fileInputRef}
            type="file"
            accept="video/*"
            onChange={handleFileInput}
            style={{ display: 'none' }}
          />
        </div>
      ) : (
        <div className="file-selected">
          <div className="file-info">
            <div className="file-icon-wrapper">
              <DropFileIcon className="file-icon" />
            </div>
            <div className="file-details">
              <p className="file-name">{selectedFile.name}</p>
              <p className="file-size">{formatFileSize(selectedFile.size)}</p>
            </div>
          </div>
          
          <div className="action-buttons">
            <button className="btn-cancel" onClick={onCancel}>
              Назад
            </button>
            <button className="btn-save">
              Отправить
            </button>
          </div>
        </div>
      )}
    </div>
  );
}
