import React from 'react';
import styled from 'styled-components';
import ColorBends from '../components/ColorBends';

const PageWrapper = styled.div`
  position: relative;
  min-height: 100vh;
  width: 100%;
`;

const BackgroundWrapper = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: -1;
`;

const Container = styled.div`
  position: relative;
  padding: 20px;
  max-width: 1200px;
  margin: 0 auto;
  z-index: 1;
`;

function HomePage() {
  return (
    <PageWrapper>
      <BackgroundWrapper>
        <ColorBends
          colors={["#ff5c7a", "#8a5cff", "#00ffd1"]}
          rotation={30}
          speed={0.3}
          scale={1.2}
          frequency={1.4}
          warpStrength={1.2}
          mouseInfluence={0.8}
          parallax={0.6}
          noise={0.08}
          transparent
        />
      </BackgroundWrapper>
      <Container>
        {/* Здесь будет содержание главной страницы */}
      </Container>
    </PageWrapper>
  );
}

export default HomePage;
