import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import RippleGrid from '../components/RippleGrid';
import GooeyNav from '../components/GooeyNav';
import Modal from '../components/Modal';
import ConfirmModal from '../components/ConfirmModal';
import UserCredentialsModal from '../components/UserCredentialsModal';
import { ReactComponent as EuroLogo } from '../assets/logos/euro-logo.svg';
import { notifyVideoApproved, notifyVideoRejected, notifyInfo, getUnreadCount } from '../utils/notificationManager';
import './AdminPage.css';

function AdminPage() {
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('users'); // users, queue, notifications
  const [users, setUsers] = useState([]);
  const [queue, setQueue] = useState([]);
  const [modalState, setModalState] = useState({
    isOpen: false,
    title: '',
    message: '',
    type: 'info'
  });

  const [credentialsModal, setCredentialsModal] = useState({
    isOpen: false,
    username: '',
    password: '',
    role: ''
  });

  // Ð¤Ð¾Ñ€Ð¼Ð° ÑÐ¾Ð·Ð´Ð°Ð½Ð¸Ñ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ
  const [newUser, setNewUser] = useState({
    firstName: '',
    lastName: '',
    middleName: '',
    role: 'user'
  });

  // Ð¤Ð¾Ñ€Ð¼Ð° Ñ€Ð°ÑÑÑ‹Ð»ÐºÐ¸ ÑƒÐ²ÐµÐ´Ð¾Ð¼Ð»ÐµÐ½Ð¸Ð¹
  const [notification, setNotification] = useState({
    recipient: 'all', // all Ð¸Ð»Ð¸ ÐºÐ¾Ð½ÐºÑ€ÐµÑ‚Ð½Ñ‹Ð¹ username
    title: '',
    message: ''
  });

  // Ð¡Ð¾ÑÑ‚Ð¾ÑÐ½Ð¸Ðµ Ð´Ð»Ñ Ð¿Ð¾ÐºÐ°Ð·Ð°/ÑÐºÑ€Ñ‹Ñ‚Ð¸Ñ Ð¿Ð°Ñ€Ð¾Ð»ÐµÐ¹
  const [visiblePasswords, setVisiblePasswords] = useState({});

  // Ð¡Ð¾ÑÑ‚Ð¾ÑÐ½Ð¸Ðµ Ð´Ð»Ñ Ð¿Ð¾Ð¸ÑÐºÐ° Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»ÐµÐ¹
  const [searchQuery, setSearchQuery] = useState('');

  // Ð¡Ð¾ÑÑ‚Ð¾ÑÐ½Ð¸Ðµ Ð´Ð»Ñ Ð¼Ð¾Ð´Ð°Ð»ÑŒÐ½Ð¾Ð³Ð¾ Ð¾ÐºÐ½Ð° Ð¿Ð¾Ð´Ñ‚Ð²ÐµÑ€Ð¶Ð´ÐµÐ½Ð¸Ñ ÑƒÐ´Ð°Ð»ÐµÐ½Ð¸Ñ
  const [deleteConfirm, setDeleteConfirm] = useState({
    isOpen: false,
    username: '',
    userFullName: ''
  });

  // Ð¡Ð¾ÑÑ‚Ð¾ÑÐ½Ð¸Ðµ Ð´Ð»Ñ Ð½ÐµÐ¿Ñ€Ð¾Ñ‡Ð¸Ñ‚Ð°Ð½Ð½Ñ‹Ñ… ÑƒÐ²ÐµÐ´Ð¾Ð¼Ð»ÐµÐ½Ð¸Ð¹
  const [unreadCount, setUnreadCount] = useState(0);

  useEffect(() => {
    // ÐŸÑ€Ð¾Ð²ÐµÑ€ÑÐµÐ¼ Ð°Ð²Ñ‚Ð¾Ñ€Ð¸Ð·Ð°Ñ†Ð¸ÑŽ Ð¸ Ð¿Ñ€Ð°Ð²Ð° Ð°Ð´Ð¼Ð¸Ð½Ð°
    const user = localStorage.getItem('user');
    if (!user) {
      navigate('/');
      return;
    }

    const userData = JSON.parse(user);
    if (userData.role !== 'admin') {
      navigate('/home');
      return;
    }

    // Ð—Ð°Ð³Ñ€ÑƒÐ¶Ð°ÐµÐ¼ ÐºÐ¾Ð»Ð¸Ñ‡ÐµÑÑ‚Ð²Ð¾ Ð½ÐµÐ¿Ñ€Ð¾Ñ‡Ð¸Ñ‚Ð°Ð½Ð½Ñ‹Ñ… ÑƒÐ²ÐµÐ´Ð¾Ð¼Ð»ÐµÐ½Ð¸Ð¹
    const count = getUnreadCount(userData.username);
    setUnreadCount(count);

    // ÐžÐ±Ð½Ð¾Ð²Ð»ÑÐµÐ¼ ÑÑ‡Ñ‘Ñ‚Ñ‡Ð¸Ðº ÐºÐ°Ð¶Ð´Ñ‹Ðµ 5 ÑÐµÐºÑƒÐ½Ð´
    const interval = setInterval(() => {
      const currentUser = localStorage.getItem('user');
      if (currentUser) {
        const currentUserData = JSON.parse(currentUser);
        const newCount = getUnreadCount(currentUserData.username);
        setUnreadCount(newCount);
      }
    }, 5000);

    loadUsers();
    loadQueue();

    return () => clearInterval(interval);
  }, [navigate]);

  const loadUsers = () => {
    const savedUsers = localStorage.getItem('users');
    if (savedUsers) {
      const usersData = JSON.parse(savedUsers);
      setUsers(usersData.users || []);
    }
  };

  const loadQueue = () => {
    const savedQueue = localStorage.getItem('adminQueue');
    const queueData = savedQueue ? JSON.parse(savedQueue) : [];
    setQueue(queueData);
  };

  const generateRandomString = (length) => {
    const chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
    let result = '';
    for (let i = 0; i < length; i++) {
      result += chars.charAt(Math.floor(Math.random() * chars.length));
    }
    return result;
  };

  const handleCreateUser = () => {
    if (!newUser.firstName || !newUser.lastName) {
      setModalState({
        isOpen: true,
        title: 'ÐžÑˆÐ¸Ð±ÐºÐ°',
        message: 'Ð—Ð°Ð¿Ð¾Ð»Ð½Ð¸Ñ‚Ðµ Ð¸Ð¼Ñ Ð¸ Ñ„Ð°Ð¼Ð¸Ð»Ð¸ÑŽ',
        type: 'error'
      });
      return;
    }

    const username = generateRandomString(8);
    const password = generateRandomString(8);

    const userToAdd = {
      username,
      password,
      role: newUser.role,
      firstName: newUser.firstName,
      lastName: newUser.lastName,
      middleName: newUser.middleName,
      createdAt: Date.now()
    };

    const updatedUsers = [...users, userToAdd];
    setUsers(updatedUsers);

    // Ð¡Ð¾Ñ…Ñ€Ð°Ð½ÑÐµÐ¼ Ð² localStorage
    localStorage.setItem('users', JSON.stringify({ users: updatedUsers }));

    // ÐŸÐ¾ÐºÐ°Ð·Ñ‹Ð²Ð°ÐµÐ¼ Ð¼Ð¾Ð´Ð°Ð»ÑŒÐ½Ð¾Ðµ Ð¾ÐºÐ½Ð¾ Ñ ÑƒÑ‡Ñ‘Ñ‚Ð½Ñ‹Ð¼Ð¸ Ð´Ð°Ð½Ð½Ñ‹Ð¼Ð¸
    setCredentialsModal({
      isOpen: true,
      username: username,
      password: password,
      role: newUser.role
    });

    // ÐžÑ‡Ð¸Ñ‰Ð°ÐµÐ¼ Ñ„Ð¾Ñ€Ð¼Ñƒ
    setNewUser({
      firstName: '',
      lastName: '',
      middleName: '',
      role: 'user'
    });
  };

  const handleDeleteUserClick = (user) => {
    if (user.username === 'Admin') {
      setModalState({
        isOpen: true,
        title: 'ÐžÑˆÐ¸Ð±ÐºÐ°',
        message: 'ÐÐµÐ»ÑŒÐ·Ñ ÑƒÐ´Ð°Ð»Ð¸Ñ‚ÑŒ Ð³Ð»Ð°Ð²Ð½Ð¾Ð³Ð¾ Ð°Ð´Ð¼Ð¸Ð½Ð¸ÑÑ‚Ñ€Ð°Ñ‚Ð¾Ñ€Ð°',
        type: 'error'
      });
      return;
    }

    // ÐžÑ‚ÐºÑ€Ñ‹Ð²Ð°ÐµÐ¼ Ð¼Ð¾Ð´Ð°Ð»ÑŒÐ½Ð¾Ðµ Ð¾ÐºÐ½Ð¾ Ð¿Ð¾Ð´Ñ‚Ð²ÐµÑ€Ð¶Ð´ÐµÐ½Ð¸Ñ
    setDeleteConfirm({
      isOpen: true,
      username: user.username,
      userFullName: `${user.lastName} ${user.firstName} ${user.middleName || ''}`.trim()
    });
  };

  const handleConfirmDelete = () => {
    const username = deleteConfirm.username;
    const updatedUsers = users.filter(u => u.username !== username);
    setUsers(updatedUsers);
    localStorage.setItem('users', JSON.stringify({ users: updatedUsers }));

    setModalState({
      isOpen: true,
      title: 'Ð£ÑÐ¿ÐµÑˆÐ½Ð¾',
      message: 'ÐŸÐ¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»ÑŒ ÑƒÐ´Ð°Ð»Ñ‘Ð½',
      type: 'success'
    });
  };

  // Ð¤Ð¸Ð»ÑŒÑ‚Ñ€Ð°Ñ†Ð¸Ñ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»ÐµÐ¹ Ð¿Ð¾ Ð¿Ð¾Ð¸ÑÐºÐ¾Ð²Ð¾Ð¼Ñƒ Ð·Ð°Ð¿Ñ€Ð¾ÑÑƒ
  const filteredUsers = users.filter(user => {
    if (!searchQuery.trim()) return true;
    
    const query = searchQuery.toLowerCase();
    return (
      user.username.toLowerCase().includes(query) ||
      user.firstName.toLowerCase().includes(query) ||
      user.lastName.toLowerCase().includes(query) ||
      (user.middleName && user.middleName.toLowerCase().includes(query))
    );
  });

  const handleApproveVideo = (video, index) => {
    // ÐžÑ‚Ð¿Ñ€Ð°Ð²Ð»ÑÐµÐ¼ ÑƒÐ²ÐµÐ´Ð¾Ð¼Ð»ÐµÐ½Ð¸Ðµ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»ÑŽ
    notifyVideoApproved(video.username, video.fileName);

    // Ð£Ð´Ð°Ð»ÑÐµÐ¼ Ð¸Ð· Ð¾Ñ‡ÐµÑ€ÐµÐ´Ð¸
    const updatedQueue = queue.filter((_, i) => i !== index);
    setQueue(updatedQueue);
    localStorage.setItem('adminQueue', JSON.stringify(updatedQueue));

    setModalState({
      isOpen: true,
      title: 'ÐžÐ´Ð¾Ð±Ñ€ÐµÐ½Ð¾',
      message: `Ð’Ð¸Ð´ÐµÐ¾ "${video.fileName}" Ð¾Ð´Ð¾Ð±Ñ€ÐµÐ½Ð¾. ÐŸÐ¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»ÑŒ Ð¿Ð¾Ð»ÑƒÑ‡Ð¸Ñ‚ ÑƒÐ²ÐµÐ´Ð¾Ð¼Ð»ÐµÐ½Ð¸Ðµ.`,
      type: 'success'
    });
  };

  const handleRejectVideo = (video, index) => {
