import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import RippleGrid from '../components/RippleGrid';
import VideoUpload from '../components/VideoUpload';
import VideoInfo from '../components/VideoInfo';
import { getVideoMetadata } from '../utils/videoValidator';
import './HomePage.css';

function HomePage() {
  const navigate = useNavigate();
  const [selectedFile, setSelectedFile] = useState(null);
  const [videoData, setVideoData] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);

  useEffect(() => {
    // Проверяем авторизацию
    const user = localStorage.getItem('user');
    if (!user) {
      navigate('/');
    }
  }, [navigate]);

  const handleFileSelect = async (file) => {
    setSelectedFile(file);
    setIsProcessing(true);

    try {
      // Получаем метаданные видео и проверяем
      const metadata = await getVideoMetadata(file);

      // Добавляем статус размера файла
      const fileSizeStatus = metadata.fileSizeValid ? 'success' : 'error';

      const videoData = {
        ...metadata,
        fileSizeStatus
      };

      setVideoData(videoData);

      // Сохраняем в историю
      saveToHistory(file.name, videoData);

      setIsProcessing(false);
    } catch (error) {
      console.error('Ошибка при обработке видео:', error);
      setIsProcessing(false);
      alert('Не удалось обработать видео. Попробуйте другой файл.');
    }
  };

  const saveToHistory = (fileName, videoData) => {
    const historyItem = {
      fileName,
      timestamp: Date.now(),
      passed: videoData.isValid,
      duration: videoData.duration,
      container: videoData.container,
      resolution: videoData.resolution,
      fileSize: `${Math.round(videoData.fileSize / 1024 / 1024)} МБ`
    };

    // Получаем текущую историю
    const savedHistory = localStorage.getItem('videoHistory');
    const history = savedHistory ? JSON.parse(savedHistory) : [];

    // Добавляем новый элемент в начало
    history.unshift(historyItem);

    // Ограничиваем историю 50 элементами
    if (history.length > 50) {
      history.pop();
    }

    // Сохраняем обратно
    localStorage.setItem('videoHistory', JSON.stringify(history));
  };

  const handleCancel = () => {
    setSelectedFile(null);
    setVideoData(null);
    setIsProcessing(false);
  };

  return (
    <div className="home-page">
      {/* Ripple Grid фон */}
      <div className="home-background">
        <RippleGrid
          enableRainbow={false}
          gridColor="#8a5cff"
          rippleIntensity={0.01}
          gridSize={20}
          gridThickness={20}
          fadeDistance={2.5}
          vignetteStrength={1.5}
          glowIntensity={0.2}
          mouseInteraction={true}
          mouseInteractionRadius={1.2}
          opacity={0.9}
        />
      </div>

      {/* Содержимое главной страницы */}
      <div className="home-content">
        <div className="content-grid">
          <VideoUpload 
            onFileSelect={handleFileSelect}
            selectedFile={selectedFile}
            onCancel={handleCancel}
          />

          <VideoInfo 
            videoData={videoData}
            isProcessing={isProcessing}
          />
        </div>
      </div>
    </div>
  );
}

export default HomePage;
