    if (!videoData) return;

    const timestamp = new Date().toLocaleString('ru-RU', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
      second: '2-digit'
    });

    const getStatusSymbol = (status) => {
      if (status === 'success') return '‚úì –ü–†–û–ô–î–ï–ù–û';
      if (status === 'error') return '‚úó –û–®–ò–ë–ö–ê';
      return '- –ù–ï –ü–†–û–í–ï–†–ï–ù–û';
    };

    const getSeparator = (char = '=', length = 70) => char.repeat(length);

    let report = '';
    
    // –ó–∞–≥–æ–ª–æ–≤–æ–∫
    report += getSeparator('=') + '\n';
    report += '                    –û–¢–ß–Å–¢ –û –ü–†–û–í–ï–†–ö–ï –í–ò–î–ï–û–§–ê–ô–õ–ê\n';
    report += getSeparator('=') + '\n\n';

    // –û–±—â–∞—è –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è
    report += '–û–ë–©–ê–Ø –ò–ù–§–û–†–ú–ê–¶–ò–Ø\n';
    report += getSeparator('-') + '\n';
    report += `–ò–º—è —Ñ–∞–π–ª–∞:       ${fileName || '–ù–µ–∏–∑–≤–µ—Å—Ç–Ω–æ'}\n`;
    report += `–î–∞—Ç–∞ –ø—Ä–æ–≤–µ—Ä–∫–∏:   ${timestamp}\n`;
    report += `–†–µ–∑—É–ª—å—Ç–∞—Ç:       ${hasErrors ? '‚úó –ù–ï –ü–†–û–ô–î–ï–ù–û' : '‚úì –£–°–ü–ï–®–ù–û –ü–†–û–ô–î–ï–ù–û'}\n`;
    report += '\n';

    // –¢–µ—Ö–Ω–∏—á–µ—Å–∫–∏–µ —Ö–∞—Ä–∞–∫—Ç–µ—Ä–∏—Å—Ç–∏–∫–∏
    report += '–¢–ï–•–ù–ò–ß–ï–°–ö–ò–ï –•–ê–†–ê–ö–¢–ï–†–ò–°–¢–ò–ö–ò\n';
    report += getSeparator('-') + '\n\n';

    // –î–ª–∏–Ω–∞ –≤–∏–¥–µ–æ
    report += `üìπ –î–ª–∏–Ω–∞ –≤–∏–¥–µ–æ\n`;
    report += `   –ó–Ω–∞—á–µ–Ω–∏–µ:  ${videoData.duration || '–ù–µ –æ–ø—Ä–µ–¥–µ–ª–µ–Ω–æ'}\n`;
    report += `   –°—Ç–∞—Ç—É—Å:    ${getStatusSymbol(videoData.durationStatus)}\n`;
    if (videoData.durationStatus === 'error') {
      report += `   –¢—Ä–µ–±—É–µ—Ç—Å—è: 15 –∏–ª–∏ 30 —Å–µ–∫—É–Ω–¥\n`;
    }
    report += '\n';

    // –ö–æ–Ω—Ç–µ–π–Ω–µ—Ä
    report += `üì¶ –ö–æ–Ω—Ç–µ–π–Ω–µ—Ä\n`;
    report += `   –ó–Ω–∞—á–µ–Ω–∏–µ:  ${videoData.container || '–ù–µ –æ–ø—Ä–µ–¥–µ–ª—ë–Ω'}\n`;
    report += `   –°—Ç–∞—Ç—É—Å:    ${getStatusSymbol(videoData.containerStatus)}\n`;
    if (videoData.containerStatus === 'error') {
      report += `   –¢—Ä–µ–±—É–µ—Ç—Å—è: MP4\n`;
    }
    report += '\n';

    // –ö–æ–¥–µ–∫
    report += `üé¨ –ö–æ–¥–µ–∫\n`;
    report += `   –ó–Ω–∞—á–µ–Ω–∏–µ:  ${videoData.codec || '–ù–µ –æ–ø—Ä–µ–¥–µ–ª—ë–Ω'}\n`;
    report += `   –°—Ç–∞—Ç—É—Å:    ${getStatusSymbol(videoData.codecStatus)}\n`;
    if (videoData.codecStatus === 'error') {
      report += `   –¢—Ä–µ–±—É–µ—Ç—Å—è: H264\n`;
    }
    report += '\n';

    // FPS
    report += `‚ö° –ß–∞—Å—Ç–æ—Ç–∞ –∫–∞–¥—Ä–æ–≤ (FPS)\n`;
    report += `   –ó–Ω–∞—á–µ–Ω–∏–µ:  ${videoData.fps || '–ù–µ –æ–ø—Ä–µ–¥–µ–ª–µ–Ω–∞'}\n`;
    report += `   –°—Ç–∞—Ç—É—Å:    ${getStatusSymbol(videoData.fpsStatus)}\n`;
    if (videoData.fpsStatus === 'error') {
      report += `   –¢—Ä–µ–±—É–µ—Ç—Å—è: 25 FPS\n`;
    }
    report += '\n';

    // –†–∞–∑—Ä–µ—à–µ–Ω–∏–µ
    report += `üìê –†–∞–∑—Ä–µ—à–µ–Ω–∏–µ\n`;
    report += `   –ó–Ω–∞—á–µ–Ω–∏–µ:  ${videoData.resolution || '–ù–µ –æ–ø—Ä–µ–¥–µ–ª–µ–Ω–æ'}\n`;
    report += `   –°—Ç–∞—Ç—É—Å:    ${getStatusSymbol(videoData.resolutionStatus)}\n`;
    if (videoData.screenInfo) {
      report += `   –≠–∫—Ä–∞–Ω:     ${videoData.screenInfo}\n`;
    }
    if (videoData.resolutionStatus === 'error') {
      report += `   –ü—Ä–∏–º–µ—á–∞–Ω–∏–µ: –†–∞–∑—Ä–µ—à–µ–Ω–∏–µ –Ω–µ —Å–æ–æ—Ç–≤–µ—Ç—Å—Ç–≤—É–µ—Ç –Ω–∏ –æ–¥–Ω–æ–º—É –∏–∑ –¥–æ—Å—Ç—É–ø–Ω—ã—Ö —ç–∫—Ä–∞–Ω–æ–≤\n`;
    }
    report += '\n';

    // –†–∞–∑–º–µ—Ä —Ñ–∞–π–ª–∞
    report += `üíæ –†–∞–∑–º–µ—Ä —Ñ–∞–π–ª–∞\n`;
    report += `   –ó–Ω–∞—á–µ–Ω–∏–µ:  ${Math.round(videoData.fileSize / 1024 / 1024)} –ú–ë\n`;
    report += `   –°—Ç–∞—Ç—É—Å:    ${getStatusSymbol(videoData.fileSizeStatus)}\n`;
    if (videoData.fileSizeStatus === 'error') {
      report += `   –¢—Ä–µ–±—É–µ—Ç—Å—è: –ú–∞–∫—Å–∏–º—É–º 400 –ú–ë\n`;
    }
    report += '\n';

    // –ò—Ç–æ–≥–æ–≤–∞—è –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è
    report += getSeparator('=') + '\n';
    report += '–ò–¢–û–ì–û–í–ê–Ø –ò–ù–§–û–†–ú–ê–¶–ò–Ø\n';
    report += getSeparator('=') + '\n\n';

    if (hasErrors) {
      report += '‚ö†Ô∏è  –í–ù–ò–ú–ê–ù–ò–ï: –§–∞–π–ª –Ω–µ –ø—Ä–æ—à—ë–ª –ø—Ä–æ–≤–µ—Ä–∫—É!\n\n';
      report += '–û–±–Ω–∞—Ä—É–∂–µ–Ω–Ω—ã–µ –ø—Ä–æ–±–ª–µ–º—ã:\n';
      let problemNumber = 1;
      
      if (videoData.durationStatus === 'error') {
        report += `  ${problemNumber++}. –ù–µ–≤–µ—Ä–Ω–∞—è –¥–ª–∏–Ω–∞ –≤–∏–¥–µ–æ\n`;
      }
      if (videoData.containerStatus === 'error') {
        report += `  ${problemNumber++}. –ù–µ–≤–µ—Ä–Ω—ã–π —Ñ–æ—Ä–º–∞—Ç –∫–æ–Ω—Ç–µ–π–Ω–µ—Ä–∞\n`;
      }
      if (videoData.codecStatus === 'error') {
        report += `  ${problemNumber++}. –ù–µ–≤–µ—Ä–Ω—ã–π –∫–æ–¥–µ–∫\n`;
      }
      if (videoData.fpsStatus === 'error') {
        report += `  ${problemNumber++}. –ù–µ–≤–µ—Ä–Ω–∞—è —á–∞—Å—Ç–æ—Ç–∞ –∫–∞–¥—Ä–æ–≤\n`;
      }
      if (videoData.resolutionStatus === 'error') {
        report += `  ${problemNumber++}. –ù–µ–ø–æ–¥—Ö–æ–¥—è—â–µ–µ —Ä–∞–∑—Ä–µ—à–µ–Ω–∏–µ\n`;
      }
      if (videoData.fileSizeStatus === 'error') {
        report += `  ${problemNumber++}. –ü—Ä–µ–≤—ã—à–µ–Ω –º–∞–∫—Å–∏–º–∞–ª—å–Ω—ã–π —Ä–∞–∑–º–µ—Ä —Ñ–∞–π–ª–∞\n`;
      }
      
      report += '\n–†–µ–∫–æ–º–µ–Ω–¥–∞—Ü–∏—è: –ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –ø—Ä–∏–≤–µ–¥–∏—Ç–µ —Ñ–∞–π–ª –≤ —Å–æ–æ—Ç–≤–µ—Ç—Å—Ç–≤–∏–µ —Å —Ç—Ä–µ–±–æ–≤–∞–Ω–∏—è–º–∏.\n';
    } else {
      report += '‚úÖ –ü–æ–∑–¥—Ä–∞–≤–ª—è–µ–º! –§–∞–π–ª —É—Å–ø–µ—à–Ω–æ –ø—Ä–æ—à—ë–ª –≤—Å–µ –ø—Ä–æ–≤–µ—Ä–∫–∏.\n';
      report += '–í–∏–¥–µ–æ –≥–æ—Ç–æ–≤–æ –∫ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏—é –Ω–∞ —Ü–∏—Ñ—Ä–æ–≤—ã—Ö —ç–∫—Ä–∞–Ω–∞—Ö.\n';
    }

    report += '\n' + getSeparator('=') + '\n';
    report += '–°–∏—Å—Ç–µ–º–∞ –ø—Ä–æ–≤–µ—Ä–∫–∏ –≤–∏–¥–µ–æ—Ñ–∞–π–ª–æ–≤ | –¢–†–¶ "–ï–≤—Ä–æ–ø–µ–π—Å–∫–∏–π"\n';
    report += getSeparator('=') + '\n';

    return report;
  };

  // –§—É–Ω–∫—Ü–∏—è —Å–∫–∞—á–∏–≤–∞–Ω–∏—è –æ—Ç—á—ë—Ç–∞
  const handleDownloadReport = () => {
    if (!videoData) return;

    const reportContent = generateReport();
    const blob = new Blob([reportContent], { type: 'text/plain; charset=utf-8' });
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    
    // –§–æ—Ä–º–∏—Ä—É–µ–º –∏–º—è —Ñ–∞–π–ª–∞
    const timestamp = new Date().toISOString().replace(/[:.]/g, '-').slice(0, -5);
    const sanitizedFileName = (fileName || 'video').replace(/[^a-zA-Z–∞-—è–ê-–Ø0-9]/g, '_');
    link.download = `–û—Ç—á—ë—Ç_${sanitizedFileName}_${timestamp}.txt`;
    
    link.href = url;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    URL.revokeObjectURL(url);
  };

  return (
    <div className="video-info-container">
      <h2 className="info-title">–î–∞–Ω–Ω—ã–µ –∑–∞–≥—Ä—É–∂–µ–Ω–Ω–æ–≥–æ —Ñ–∞–π–ª–∞</h2>
      
      {!videoData && !isProcessing ? (
        <div className="empty-state">
          <p className="empty-text">Empty</p>
        </div>
      ) : isProcessing ? (
        <div className="processing-state">
          <div className="spinner"></div>
          <p>–û–±—Ä–∞–±–æ—Ç–∫–∞ –≤–∏–¥–µ–æ...</p>
        </div>
      ) : (
        <div className="info-list">
          <div className={`info-item ${videoData.durationStatus || 'success'}`}>
            <span className="info-label">–î–ª–∏–Ω–∞ –≤–∏–¥–µ–æ:</span>
            <span className="info-value">{videoData.duration || '15 —Å–µ–∫—É–Ω–¥'}</span>
          </div>
          
          <div className={`info-item ${videoData.containerStatus || 'success'}`}>
            <span className="info-label">–ö–æ–Ω—Ç–µ–π–Ω–µ—Ä:</span>
            <span className="info-value">{videoData.container || '.mp4'}</span>
          </div>
          
          <div className={`info-item ${videoData.codecStatus || 'success'}`}>
            <span className="info-label">–ö–æ–¥–µ–∫:</span>
            <span className="info-value">{videoData.codec || 'H264'}</span>
          </div>
          
          <div className={`info-item ${videoData.fpsStatus || 'success'}`}>
            <span className="info-label">–ß–∞—Å—Ç–æ—Ç–∞ –∫–∞–¥—Ä–æ–≤:</span>
            <span className="info-value">{videoData.fps || '25'}</span>
          </div>
          
          <div className={`info-item ${videoData.resolutionStatus || 'success'}`}>
            <span className="info-label">–†–∞–∑—Ä–µ—à–µ–Ω–∏–µ:</span>
            <span className="info-value">{videoData.resolution || '2560x1440 px'}</span>
          </div>
          
          {videoData.screenInfo && (
            <div className={`info-item ${videoData.matchingScreen ? 'success' : 'error'}`}>
              <span className="info-label">–ü–æ–¥—Ö–æ–¥—è—â–∏–π —ç–∫—Ä–∞–Ω:</span>
              <span className="info-value">{videoData.screenInfo}</span>
            </div>
          )}
          
          <div className={`status-badge ${hasErrors ? 'error' : 'success'}`}>
            <span className="status-icon">{hasErrors ? '‚úó' : '‚úì'}</span>
            {hasErrors ? '–ü—Ä–æ–≤–µ—Ä–∫–∞ –Ω–µ –ø—Ä–æ–π–¥–µ–Ω–∞' : '–ü—Ä–æ–≤–µ—Ä–∫–∞ —É—Å–ø–µ—à–Ω–æ –ø—Ä–æ–π–¥–µ–Ω–∞'}
          </div>
          
          <button className="btn-download" onClick={handleDownloadReport}>
            –°–∫–∞—á–∞—Ç—å –æ—Ç—á—ë—Ç
          </button>
        </div>
      )}
    </div>
  );
}
