import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import RippleGrid from '../components/RippleGrid';
import GooeyNav from '../components/GooeyNav';
import { ReactComponent as EuroLogo } from '../assets/logos/euro-logo.svg';
import { getUserNotifications, markAsRead, markAllAsRead, deleteNotification } from '../utils/notificationManager';
import './NotificationsPage.css';

function NotificationsPage() {
  const navigate = useNavigate();
  const [notifications, setNotifications] = useState([]);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [notificationToDelete, setNotificationToDelete] = useState(null);

  useEffect(() => {
    // –ü—Ä–æ–≤–µ—Ä—è–µ–º –∞–≤—Ç–æ—Ä–∏–∑–∞—Ü–∏—é
    const user = localStorage.getItem('user');
    if (!user) {
      navigate('/');
      return;
    }

    // –ó–∞–≥—Ä—É–∂–∞–µ–º —É–≤–µ–¥–æ–º–ª–µ–Ω–∏—è
    loadNotifications();
  }, [navigate]);

  const loadNotifications = () => {
    const user = JSON.parse(localStorage.getItem('user'));
    const username = user?.username || 'unknown';
    const userNotifications = getUserNotifications(username);
    setNotifications(userNotifications);
  };

  const handleMarkAsRead = (notificationId) => {
    markAsRead(notificationId);
    loadNotifications();
  };

  const handleMarkAllAsRead = () => {
    const user = JSON.parse(localStorage.getItem('user'));
    const username = user?.username || 'unknown';
    markAllAsRead(username);
    loadNotifications();
  };

  const handleDeleteClick = (notification) => {
    setNotificationToDelete(notification);
    setShowDeleteModal(true);
  };

  const handleConfirmDelete = () => {
    if (notificationToDelete) {
      deleteNotification(notificationToDelete.id);
      loadNotifications();
      setShowDeleteModal(false);
      setNotificationToDelete(null);
    }
  };

  const handleCancelDelete = () => {
    setShowDeleteModal(false);
    setNotificationToDelete(null);
  };

  const handleBack = () => {
    navigate('/home');
  };

  const handleNavClick = (item) => {
    if (item.action === 'home') {
      navigate('/home');
    } else if (item.action === 'history') {
      navigate('/history');
    } else if (item.action === 'notifications') {
      // –£–∂–µ –Ω–∞ —Å—Ç—Ä–∞–Ω–∏—Ü–µ —É–≤–µ–¥–æ–º–ª–µ–Ω–∏–π
      return;
    } else if (item.action === 'logout') {
      localStorage.removeItem('user');
      navigate('/');
    }
  };

  const navItems = [
    { label: '–ì–ª–∞–≤–Ω–∞—è', href: '#', action: 'home' },
    { label: '–ò—Å—Ç–æ—Ä–∏—è', href: '#', action: 'history' },
    { label: '–£–≤–µ–¥–æ–º–ª–µ–Ω–∏—è', href: '#', action: 'notifications' },
    { label: '–í—ã—Ö–æ–¥', href: '#', action: 'logout' }
  ];

  return (
    <div className="notifications-page">
      {/* Ripple Grid —Ñ–æ–Ω */}
      <div className="notifications-background">
        <RippleGrid
          enableRainbow={false}
          gridColor="#8a5cff"
          rippleIntensity={0.01}
          gridSize={20}
          gridThickness={20}
          fadeDistance={2.5}
          vignetteStrength={1.5}
          glowIntensity={0.2}
          mouseInteraction={true}
          mouseInteractionRadius={1.2}
          opacity={0.9}
        />
      </div>

      {/* –õ–æ–≥–æ—Ç–∏–ø –∏ –Ω–∞–≤–∏–≥–∞—Ü–∏—è */}
      <div className="page-header">
        <div className="page-logo">
          <EuroLogo />
        </div>
        <GooeyNav
          items={navItems}
          particleCount={15}
          particleDistances={[90, 10]}
          particleR={100}
          initialActiveIndex={2}
          animationTime={600}
          timeVariance={300}
          colors={[1, 2, 3, 1, 2, 3, 1, 4]}
          onItemClick={handleNavClick}
        />
      </div>

      {/* –°–æ–¥–µ—Ä–∂–∏–º–æ–µ —Å—Ç—Ä–∞–Ω–∏—Ü—ã —É–≤–µ–¥–æ–º–ª–µ–Ω–∏–π */}
      <div className="notifications-content">
        <div className="notifications-header">
          <button className="btn-back" onClick={handleBack}>
            ‚Üê –ù–∞–∑–∞–¥
          </button>
          <h1 className="notifications-title">–£–≤–µ–¥–æ–º–ª–µ–Ω–∏—è</h1>
        </div>

        <div className="notifications-list">
          {notifications.length === 0 ? (
            <div className="empty-notifications">
              <div className="empty-icon">üîî</div>
              <p>–£ –≤–∞—Å –ø–æ–∫–∞ –Ω–µ—Ç —É–≤–µ–¥–æ–º–ª–µ–Ω–∏–π</p>
            </div>
          ) : (
            notifications.map((notification, index) => (
              <div key={index} className="notification-item">
                <div className="notification-content">
                  <h3>{notification.title}</h3>
                  <p>{notification.message}</p>
                  <span className="notification-time">
                    {new Date(notification.timestamp).toLocaleString('ru-RU')}
                  </span>
                </div>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
}

export default NotificationsPage;
